export const getExam = (year: number | null, subject: string | null, language: 'english' | 'somali' = 'english'): Exam | undefined => {
  if (year === 2025) {
    if (subject === 'Physics') return PHYSICS_2025_EXAM;
    if (subject === 'Arabic Language') return ARABIC_2025_EXAM;
    if (subject === 'History' && language === 'somali') return HISTORY_2025_SOMALI_EXAM;
    if (subject === 'Business') return BUSINESS_2025_EXAM;
    if (subject === 'Geography') return GEOGRAPHY_2025_EXAM;
    if (subject === 'ICT') return ICT_2025_EXAM;
  }
  return undefined;
};
